mapper.update:
	UPDATE device a 
    SET a.device_status=#{deviceStatus}
    WHERE a.device_id_jp=#{deviceIdJp} AND a.device_id_supp=#{deviceIdSupp}

mapper.select:
SELECT a.run_status 
FROM device a 
WHERE a.device_id_jp=#{deviceIdJp} AND a.device_id_supp=#{deviceIdSupp}

 SELECT b.apk_version,b.apk_url
  FROM device a INNER JOIN  apk_version b ON a.apk_version_id=b.id
WHERE a.device_id_jp=#{deviceIdJp} AND a.device_id_supp=#{deviceIdSupp}
