mapper.select:
SELECT    FROM device a 
INNER JOIN device_model b ON a.device_model_id=b.id
WHERE a.id = #{_parameter}

SELECT    FROM device a 
INNER JOIN device_model b ON a.device_model_id=b.id
WHERE a.up_time < #{_parameter}

SELECT    FROM device a 
INNER JOIN device_model b ON a.device_model_id=b.id

  AND device_id_jp=#{deviceIdJp}
  AND device_id_supp=#{deviceIdSupp}

SELECT COUNT(id) FROM device 

  AND device_id_jp=#{deviceIdJp}
  AND device_id_supp=#{deviceIdSupp}

mapper.update:
UPDATE device a
a.short_name=#{shortName}, a.location=#{location}, a.device_status=#{deviceStatus}, a.up_time=#{upTime}, a.sign_code=#{signCode}, a.device_supp_id=#{deviceIdSupp}, a.device_model_id=#{deviceModelId}, a.lock_status=#{lockStatus}, a.update_by = #{updateBy}, update_date = now(),
AND id =#{id} AND device_id_jp =#{deviceIdJp} AND device_id_supp =#{deviceIdSupp}
