mapper.select:
    SELECT  FROM goods_ads_top a 
LEFT JOIN  goods_sku b ON b.goods_code=a.goods_code 
LEFT JOIN device_road c ON c.goods_sku_id=b.id
LEFT JOIN device d ON  d.id=a.device_id

    AND d.device_id_jp=#{deviceIdJp}
    AND d.device_id_supp=#{deviceIdSupp}

SELECT FROM goods_ads_top a LEFT JOIN goods_sku b ON b.goods_code = a.goods_code INNER JOIN device d ON d.id = a.device_id LEFT JOIN device_road c ON c.goods_sku_id = b.id AND c.device_id=d.id
AND d.id=#{deviceId}
GROUP BY a.goods_code,a.device_id

mapper.update:
UPDATE goods_ads_top a
a.goods_code=#{goodsCode}, a.action_type=#{actionType}, a.device_id=#{deviceId}, a.big_pic_url=#{bigPicUrl}, a.tiny_pic_url=#{tinyPicUrl}, a.update_by = #{updateBy}, update_date = now(),
AND a.id =#{id}

mapper.select:
SELECT COUNT(id) FROM goods_ads_top

  AND device_id=#{deviceId}
  AND goods_code=#{goodsCode}
