mapper.select:
 SELECT 
FROM goods_storage_out a 
 INNER JOIN goods_sku b ON a.goods_sku_id = b.id 
 INNER JOIN device c ON c.id = a.device_id 
 INNER JOIN sys_user d ON d.id=a.apply_by
 INNER JOIN goods_storage_in e ON e.id=a.goods_storage_in_id 


  AND b.id=#{goodsSkuId}


  AND c.id=#{deviceId}

   AND a.apply_date >=#{startApplyDate}
   AND a.apply_date <#{endApplyDate}

 ORDER BY a.apply_date DESC

SELECT FROM goods_storage_out a INNER JOIN goods_sku b ON a.goods_sku_id = b.id INNER JOIN device c ON c.id = a.device_id INNER JOIN goods_storage_in e ON e.id=a.goods_storage_in_id
AND b.goods_code=#{goodsCode}
AND c.id=#{deviceId}
ORDER BY a.apply_date DESC
