mapper.select:
SELECT FROM goods_storage_rd a INNER JOIN goods_sku b ON a.goods_sku_id = b.id INNER JOIN goods_storage_in c ON c.id=a.goods_storage_in_id
AND a.goods_sku_id=#{goodsSkuId}
ORDER BY a.rd_day DESC

mapper.update:
UPDATE goods_storage_in a INNER JOIN goods_sku b ON a.goods_sku_id = b.id
a.curr_count=a.curr_count+#{currCount}, a.goods_storage_location_id=#{goodsStorageLocationId}, a.update_date=now(),
AND a.id=#{id}
