mapper.select:
    SELECT a.goods_name goodsName,a.sale_price salePrice,a.pay_type payType 
    FROM order_list a 


				AND a.order_status=#{orderStatus}


				AND a.order_id=#{order}

mapper.update:
UPDATE order_list a
a.wx_prepay_id=#{wxPrepayId}, a.pay_type=#{payType},
WHERE a.order_id=#{orderId}
