mapper.select:
 SELECT   FROM #{prefix}_order_list a 
 INNER JOIN device b ON a.device_id_jp= b.device_id_jp

   AND short_name LIKE "%" #{shortName} "%"
   AND order_time >=#{startOrderTime}
   AND order_time <#{endOrderTime}
   AND b.id =#{deviceId}
   AND a.device_id_jp =#{deviceIdJp}
   AND a.order_type =#{orderType}
   AND a.create_by =#{createBy}
 
 ORDER BY a.order_time DESC

 SELECT COUNT(*)  FROM #{prefix}_order_list a 
 INNER JOIN device b ON a.device_id_jp= b.device_id_jp

   AND short_name LIKE "%" #{shortName} "%"
   AND order_time >=#{startOrderTime}
   AND order_time <#{endOrderTime}
   AND b.id =#{deviceId}
   AND a.device_id_jp =#{deviceIdJp}
   AND a.order_type =#{orderType}
   AND a.create_by =#{createBy}

SELECT a.goods_name goodsName, a.sale_price salePrice, a.pay_type payType, a.order_id orderId, a.device_id_jp deviceIdJp, a.device_id_supp deviceIdSupp, a.ctn, a.floor, a.order_type orderType, a.path_code pathCode FROM ${prefix}_order_list a
AND a.order_status=${orderStatus}
AND a.order_id='${order}'
