mapper.select:
  SELECT    FROM  report_base a 
INNER JOIN device b  ON a.device_id = b.id 
INNER JOIN goods_sku c ON c.goods_code = a.goods_code 

    AND b.id=#{deviceId}
    AND c.goods_code=#{goodsCode}
    AND a.order_day>=#{startOrderDay}
    AND a.order_day<=#{endOrderDay}

 ORDER BY a.order_day DESC,b.short_name,a.sales_count DESC

SELECT b.device_id_supp, b.short_name, c.goods_name, c.goods_code, SUM(a.sales_count) sales_count, SUM(a.sales_amount) sales_amount, SUM(a.costs_amount) costs_amount, ROUND(a.sales_amount/a.sales_count,2) averageSalePrice, ROUND(a.costs_amount/a.sales_count,2) averageCostPrice, (SUM(a.sales_amount)-SUM(a.costs_amount)) profit, CONCAT(ROUND(ROUND((SUM(a.sales_amount)-SUM(a.costs_amount))/SUM(a.costs_amount),2)*100,0),'%') costProfit, CONCAT(ROUND(ROUND((SUM(a.sales_amount)-SUM(a.costs_amount))/SUM(a.sales_amount),2)*100,0),'%') salesProfit FROM report_base a INNER JOIN device b ON a.device_id = b.id INNER JOIN goods_sku c ON c.goods_code = a.goods_code
AND b.id=#{deviceId} AND a.order_day>=#{startOrderDay} AND a.order_day<=#{endOrderDay}
GROUP BY a.goods_code,a.device_id ORDER BY b.short_name,sales_count DESC

SELECT 
  SUM(a.sales_count) sales_count,
  SUM(a.sales_amount) sales_amount 
FROM
  report_base a INNER JOIN device b ON b.id=a.device_id
  INNER JOIN goods_sku c ON c.goods_code=a.goods_code

      AND b.id=#{deviceId}
      AND c.goods_code=#{goodsCode}
      AND a.order_day>=#{startOrderDay}
      AND a.order_day<=#{endOrderDay}
